import React, {useState} from "react";

const useStateEx = () => {
  const [counter, setCounter] = useState(0);
  return (
    <div>
      <div>{counter}</div>
      <button
        type="button"
        onClick={() => {
          setCounter((incCounter) => incCounter + 1);
        }}
      >
        +1
      </button>
      <button
        type="button"
        onClick={() => {
          setCounter((incCounter) => incCounter - 1);
        }}
      >
        -1
      </button>
    </div>
  );
};

export default useStateEx;

